export default function add(a, b) {
    let i = a.length - 1, j = b.length - 1;
    let carry = 0, ret = '';

    while (i >= 0 || j >= 0) {
        let x = 0, y = 0, sum = 0;

        if (i >= 0) {
            x = a[i] - 0;
            i --;
        }

        if (j >= 0 ) {
            y = b[j] - 0;
            j --;
        }

        sum = x + y + carry;

        if (sum >= 10) {
            sum -= 10;
            carry = 1;
        } else {
            carry = 0;
        }

        ret = sum + ret;
    }

    if (carry) {
        ret = 1 + ret;
    }

    return ret
}